const data = $input.first().json;

// 피드백 검증
if (!data.automation_id || data.rating === undefined) {
  throw new Error('automation_id와 rating은 필수입니다.');
}

// rating: 1 (긍정), -1 (부정)
const rating = data.rating > 0 ? 1 : -1;

// 시너지율 조정 계산
// 긍정: +2%, 부정: -5%
const synergyAdjustment = rating === 1 ? 0.02 : -0.05;

return [{
  json: {
    automation_id: data.automation_id,
    rating,
    comment: data.comment || '',
    synergy_adjustment: synergyAdjustment,
    processed_at: new Date().toISOString()
  }
}];

